    // setting system properties is necessary for use with VisiBroker for Java in Java 1.2
    Properties props = System.getProperties ();
    props.put ("org.omg.CORBA.ORBClass", "com.visigenic.vbroker.orb.ORB");
    props.put ("org.omg.CORBA.ORBSingletonClass", "com.visigenic.vbroker.orb.ORB");
    System.setProperties (props);



    BOA boa = ((com.visigenic.vbroker.orb.ORB)orb).BOA_init ();



    boa.obj_is_ready (/* add your object implementation here */);



    boa.impl_is_ready ();








    // server will use VisiBroker proprietary binder
    // you must instantiate your implementation like this:
    // HelloWorld hello = new HelloWorldImpl ("HelloWorld");
    // in brackets is name which is after starting visible from implementation repository






    // client will use VisiBroker proprietary binder
    // you must bind to your object implementation
    // using code which looks like this:
    // HelloWorld hello = HelloWorldHelper.bind (orb, "HelloWorld");
    // then you can invoke the method:  hello.say ()  etc.
